package com.selfstudy;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 9,b=12;
		int c = a&b;
		int e = a|b;
		int f = a^b;
		System.out.println("a&b is= "+ c);
		System.out.println("a/b is = "+ e);
		System.out.println("a^b is = "+ f);
		

	}

}
